def file_editor(file_path, content):
    try:
        with open(file_path, 'w') as file:
            file.write(content)
        return True
    except Exception as e:
        return False

def web_browser(url):
    import requests
    try:
        response = requests.get(url)
        return response.text
    except Exception as e:
        return str(e)

