def read_file(file_path, encoding='utf-8'):
    try:
        with open(file_path, 'r', encoding=encoding) as file:
            return file.read()
    except FileNotFoundError:
        return f'Error: File {file_path} not found'
    except Exception as e:
        return f'Error reading file: {str(e)}'

import pandas as pd

def analyze_csv(file_path):
    try:
        df = pd.read_csv(file_path)
        df['failure_rate'] = df['failed'] / df['total']
        trend = 'increasing' if df['failure_rate'].iloc[-1] > df['failure_rate'].iloc[0] else 'decreasing'
        return {
            'failure_rates': df[['date', 'failure_rate']].to_dict(orient='records'),
            'trend': trend
        }
    except FileNotFoundError:
        return {'error': f'Error: File {file_path} not found'}
    except Exception as e:
        return {'error': str(e)}

def write_markdown(file_path, content):
    try:
        with open(file_path, 'w') as file:
            file.write(content)
        return f'Successfully wrote content to {file_path}'
    except Exception as e:
        return f'Error writing markdown file: {str(e)}'
