def read_file(file_path, encoding='utf-8'):
    try:
        with open(file_path, 'r', encoding=encoding) as f:
            return f.read()
    except Exception as e:
        return f'Error reading file {file_path}: {str(e)}'

def write_file(file_path, content):
    try:
        with open(file_path, 'w') as f:
            f.write(content)
        return f'Successfully wrote to {file_path}'
    except Exception as e:
        return f'Error writing file {file_path}: {str(e)}'

import pandas as pd
def analyze_csv(file_path):
    try:
        data = pd.read_csv(file_path)
        data['failure_rate'] = data['failed'] / data['total']
        trend = 'improving' if data['failure_rate'].iloc[-1] < data['failure_rate'].iloc[0] else 'worsening'
        return {'failure_rates': data[['date', 'failure_rate']].to_dict(orient='records'), 'trend': trend}
    except Exception as e:
        return f'Error analyzing CSV file {file_path}: {str(e)}'
