{
  "tools": [
    {
      "id": "file_writer",
      "name": "write_file",
      "description": "Writes content to a file with proper error handling",
      "parameters": [
        {
          "name": "file_path",
          "type": "string",
          "description": "Path to the file where content will be written",
          "required": true
        },
        {
          "name": "content",
          "type": "string",
          "description": "Content to write to the file",
          "required": true
        },
        {
          "name": "mode",
          "type": "string",
          "description": "Mode for writing: 'w' for overwrite, 'a' for append",
          "required": false,
          "default": "w"
        }
      ],
      "returns": {
        "type": "string",
        "description": "Success message or error"
      },
      "code": "def write_file(file_path, content, mode='w'):\n    try:\n        with open(file_path, mode) as file:\n            file.write(content)\n        return f'Successfully wrote to {file_path}'\n    except Exception as e:\n        return f'Error writing file: {str(e)}'"
    },
    {
      "id": "file_reader",
      "name": "read_file",
      "description": "Reads content from a file",
      "parameters": [
        {
          "name": "file_path",
          "type": "string",
          "description": "Path to the file to be read",
          "required": true
        }
      ],
      "returns": {
        "type": "string",
        "description": "File content or error message"
      },
      "code": "def read_file(file_path):\n    try:\n        with open(file_path, 'r') as file:\n            return file.read()\n    except FileNotFoundError:\n        return f'Error: File {file_path} not found'\n    except Exception as e:\n        return f'Error reading file: {str(e)}'"
    },
    {
      "id": "python_analyzer",
      "name": "analyze_code",
      "description": "Analyzes Python code for bugs and issues",
      "parameters": [
        {
          "name": "code",
          "type": "string",
          "description": "Python code to analyze",
          "required": true
        }
      ],
      "returns": {
        "type": "object",
        "description": "Analysis results including errors and warnings"
      },
      "code": "def analyze_code(code):\n    import ast\n    result = {'errors': [], 'warnings': []}\n    try:\n        tree = ast.parse(code)\n    except SyntaxError as e:\n        result['errors'].append({'line': e.lineno, 'message': e.msg})\n    return result"
    }
  ]
}
END_ARG
END_TOOL_CALL

<FINISHED>

