def configure_team(team_structure):
    try:
        # Convert dictionary to JSON and write to file
        import json
        with open('team.json', 'w') as file:
            json.dump(team_structure, file, indent=2)
        return 'Team structure configured successfully.'
    except Exception as e:
        return f'Error configuring team: {str(e)}'
